mapper.select:
SELECT FROM prompt WHERE 1=1
AND prompt_key = #{promptKey}
AND prompt_key LIKE CONCAT('%', #{promptKey}, '%')
AND (tags = #{tag} OR tags LIKE CONCAT('%,', #{tag}, ',%') OR tags LIKE CONCAT(#{tag}, ',%') OR tags LIKE CONCAT('%,', #{tag}))
ORDER BY create_time DESC LIMIT #{offset}, #{limit}

mapper.update:
UPDATE prompt
prompt_desc = #{promptDesc}, tags = #{tags}, update_time = NOW()
WHERE prompt_key = #{promptKey}

UPDATE prompt
SET latest_version = #{latestVersion}, update_time = NOW()
WHERE prompt_key = #{promptKey}

mapper.select:
SELECT p.id, p.prompt_key, p.prompt_desc, p.latest_version, p.tags, p.create_time, p.update_time, pv.status as latest_version_status FROM prompt p LEFT JOIN prompt_version pv ON p.prompt_key = pv.prompt_key AND p.latest_version = pv.version WHERE 1=1
AND p.prompt_key = #{promptKey}
AND p.prompt_key LIKE CONCAT('%', #{promptKey}, '%')
AND (p.tags = #{tag} OR p.tags LIKE CONCAT('%,', #{tag}, ',%') OR p.tags LIKE CONCAT(#{tag}, ',%') OR p.tags LIKE CONCAT('%,', #{tag}))
ORDER BY p.create_time DESC LIMIT #{offset}, #{limit}
